package homeAssignment.week1;

public class FindOddNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int maxRange =10;

		for(int i=0; i<=maxRange; i++) {
			if(i%2==1) {

				System.out.println("The Number " + i + " is odd");
			}

		}


	}

	}
